export const i18nControllerTranslate = <ThrowOnError extends boolean = false>(options: Options<I18nControllerTranslateData, ThrowOnError>) => (options.client ?? client).post<I18nControllerTranslateResponses, unknown, ThrowOnError>({
    security: [{ scheme: 'bearer', type: 'http' }],
    url: '/nova-admin-api/i18n/translate',
    ...options,
    headers: {
        'Content-Type': 'application/json',
        ...options.headers
    }
});
